import {useState,useEffect} from 'react'
import { useNavigate } from 'react-router-dom'
import {v4 as uuidv4} from "uuid"
import Cookies from 'js-cookie'

const UserDetails = () => {
    const navigate = useNavigate()
    const [firstname,setFirstName] = useState("")
    const [lastname,setLastName] = useState("")
    const [email,setemail] = useState("")
    const [location,setLocation] = useState("")
    const [occupation,setOccupation] = useState("")
    const [number,setNumber] = useState("")
    const [gender,setGender] = useState("Male")

    const getFirstName = event => {
        setFirstName(event.target.value)
    }

    const getLastName = event => {
        setLastName(event.target.value)
    }

    const getEmail = event =>{
        setemail(event.target.value)
    }

    const getLocation = event => {
        setLocation(event.target.value)
    }

    const getOccupation = event => {
        setOccupation(event.target.value)
    }

    const getNumber = event => {
        setNumber(event.target.value)
    }

    const getGender = event => {
        setGender(event.target.value)
    }

    const uploadTheData = async event => {
        event.preventDefault()
        const user_id = Cookies.get('user_id')
        const userGivenData = {
            "id":uuidv4(),
            "username":firstname+" "+lastname,
            "email":email,
            "location":location,
            "occupation":occupation,
            "number":number,
            "gender":gender,
            "user_id":user_id
        }
        const url = 'http://localhost:5000/user_input/'
        const options = {
            method:"POST",
            headers:{
                "Content-Type": "application/json"
            },
            body: JSON.stringify(userGivenData)
        }
        const response = await fetch(url,options)
        const responseData = await response.json()
        console.log(responseData.message) 
        navigate('/')
    }

    

    return(
        <form onSubmit={uploadTheData}>
            <label htmlFor="firstName">First Name</label>
            <input type="text" id="firstName" placeholder='First Name'value={firstname} onChange={getFirstName}/>
            <label htmlFor="lastName">Last Name</label>
            <input type="text" id="lastName" placeholder='Last Name'value={lastname} onChange={getLastName}/>
            <label htmlFor="email">Email</label>
            <input type="text" id="email" placeholder='Email' value={email} onChange={getEmail}/>
            <label htmlFor="location">Location</label>
            <input type="text" id="location" placeholder='Location' value={location} onChange={getLocation}/>
            <label htmlFor="occupation">Occupation</label>
            <input type="text" id="occupation" placeholder='Occupation' value={occupation} onChange={getOccupation}/>
            <label htmlFor="number">Number</label>
            <input type="number" id="number" placeholder='Number' value={number} onChange={getNumber}/>
            <label htmlFor='gender'>Gender</label>
            <select id="gender" onChange={getGender}>
                <option>Male</option>
                <option>Female</option>
                <option>other</option>
            </select>
            <button type="submit">Submit</button>
        </form>
    )
}

export default UserDetails
