import {Component} from 'react'
import {v4 as uuidv4} from 'uuid'
import { Navigate } from 'react-router-dom';
import { IoMdEye } from "react-icons/io";
import { IoIosEyeOff } from "react-icons/io";

class Register extends Component{
    state = {username:"",password:"",email:"",number:"",errMsg:"",isShown:false,shouldRedirect:false}


    changevalue = () => {
        this.setState((prev)=>({
            isShown:!prev.isShown
        }))
    }

    getUserName = (event) => {
        this.setState({username:event.target.value})
    }

    getPassword = (event) => {
        this.setState({password:event.target.value})
    }

    getEmail = event => {
        this.setState({email:event.target.value})
    }

    getNumber = event => {
        this.setState({number:event.target.value})
    }

    submitData = async event => {
        event.preventDefault()
        const {username,password,email,number} = this.state
        const opdata = {
            "id": uuidv4(),
            "username":username,
            "password":password,
            "email":email,
            "number":number
        }
        const url = "http://localhost:5000/register/"
        const options = {
            method: "POST",
            headers: {
                "Content-Type": "application/json"
            },
            body: JSON.stringify(opdata)
        }
        const respone = await fetch(url,options)
        console.log(respone)
        if(respone.ok===true){
            const responseData = await respone.json()
            console.log(responseData)
            if(responseData.message==="success"){
                this.setState({errorMsg:"",shouldRedirect:true})   
            }else{
                this.setState({errorMsg:responseData.message})
            }
        }
        
        
    }
    render(){
        const {username,email,number,errorMsg,isShown,shouldRedirect} = this.state

        if(shouldRedirect){
            return <Navigate to="login"/>
        }

        return(
            <form onSubmit={this.submitData}>
                <label htmlFor="username">USERNAME</label>
                <input type="text" value={username} placeholder='Username should be 14 characters' id="username" onChange={this.getUserName}/>
                <label htmlFor="password">PASSWORD</label>
                <input type={isShown===true ? "text" : "password"}  placeholder='Password' id="password" onChange={this.getPassword}/>
                <button onClick={this.changevalue}>
                    {isShown===true ? <IoIosEyeOff/> : <IoMdEye/>}
                </button>
                <label htmlFor="email">EMAIL</label>
                <input type="text" value={email} placeholder='Email' id="email" onChange={this.getEmail}/>
                <label htmlFor="number">MOBILE NUMBER</label>
                <input type="number" value={number} placeholder='Mobile Number' id="number" onChange={this.getNumber}/>
                <button type="submit">Submit</button>
                <p>{errorMsg}</p>
            </form>
        )
    }
}

export default Register
