import {useState,useEffect} from 'react'
import {Navigate} from 'react-router-dom'
import Cookies from 'js-cookie'
import { FaTrashAlt } from "react-icons/fa";

const DisplayData = props => {
    const {datas,onDelete,onUpdate} = props 
    const{id,username,email,location,gender,occupation,number} = datas
    const [isEditeds,changeEdits] = useState(false)

    const editTheData = () => {
        changeEdits(true)
    }

    useEffect(()=>{
        setFirstName(username)
        setGender(gender)
        setLocation(location)
        setNumber(number)
        setemail(email)
        setOccupation(occupation)
    },[datas])

    const [usernames,setFirstName] = useState(username)
    const [emails,setemail] = useState(email)
    const [locations,setLocation] = useState(location)
    const [occupations,setOccupation] = useState(occupation)
    const [numbers,setNumber] = useState(number)
    const [genders,setGender] = useState(gender)

    const getFirstName = event => {
        setFirstName(event.target.value)
    }

    const getEmail = event =>{
        setemail(event.target.value)
    }

    const getLocation = event => {
        setLocation(event.target.value)
    }

    const getOccupation = event => {
        setOccupation(event.target.value)
    }

    const getNumber = event => {
        setNumber(event.target.value)
    }

    const getGender = event => {
        setGender(event.target.value)
    }

    

    const uploadTheDatas = async event => {
        event.preventDefault()
        const user_id = Cookies.get('user_id')
        const url = "http://localhost:5000/update/"
        const newData = {
            "id":id,
            "user_id":user_id,
            "username":usernames,
            "email":emails,
            "location":locations,
            "gender":genders,
            "occupation":occupations,
            "number":numbers
        }
        const options = {
            method: "PUT",
            headers: {
                "Content-Type": "application/json"
            },
            body:JSON.stringify(newData)
        }
        await fetch(url,options)
        changeEdits(false)
    }



    const deleteTheData = async () => {
        const url = `http://localhost:5000/delete/${id}`
        console.log(id)
        const options = {
            method: "DELETE"
        } 
        await fetch(url,options)
        onDelete()
    }

    const renderData = () => {
        if(isEditeds===false){
            return(
                <li>
                    <p>{username}</p>
                    <p>{email}</p>
                    <p>{location}</p>
                    <p>{gender}</p>
                    <p>{occupation}</p>
                    <p>{number}</p>
                    <button onClick={editTheData}>Edit</button>
                    <button onClick={deleteTheData}><FaTrashAlt/></button>
                </li>
            )
        }else{
            return(
                <>
                    <form onSubmit={uploadTheDatas}>
            <label htmlFor="firstName">Username</label>
            <input type="text" id="firstName" placeholder='First Name'value={usernames} onChange={getFirstName}/>
            <label htmlFor="email">Email</label>
            <input type="text" id="email" placeholder='Email' value={emails} onChange={getEmail}/>
            <label htmlFor="location">Location</label>
            <input type="text" id="location" placeholder='Location' value={locations} onChange={getLocation}/>
            <label htmlFor="occupation">Occupation</label>
            <input type="text" id="occupation" placeholder='Occupation' value={occupations} onChange={getOccupation}/>
            <label htmlFor="number">Number</label>
            <input type="number" id="number" placeholder='Number' value={numbers} onChange={getNumber}/>
            <label htmlFor='gender'>Gender</label>
            <input type="text" id="gender" placeholder='Gender' value={genders} onChange={getGender}/>
            <button type="submit">Edit</button>
        </form>
                </>
            ) 
        }
    }
    
    return(
       <>
        {renderData()}
       </>
    )

}
export default DisplayData
