import {Component} from 'react'
import { IoMdEye } from "react-icons/io";
import { IoIosEyeOff } from "react-icons/io";

class Login extends Component{
    state = {username:"",password:"",errorMsg:"",isShowns:false}

    getUserNameLogin = event => {
        this.setState({username:event.target.value})
    }

    getPasswordLogin = event => {
        this.setState({password:event.target.value})
    }
    changevalueLogin = () => {
        this.setState((prev)=>({
            isShowns:!prev.isShowns
        }))
    }

    render(){
        const {username,isShowns} = this.state
        return(
            <form onSubmit={this.submitDataLogin}>
                <label htmlFor="username">USERNAME</label>
                <input type="text" value={username} placeholder='Username' id="username" onChange={this.getUserNameLogin}/>
                <label htmlFor="password">PASSWORD</label>
                <input type={isShown===true ? "text" : "password"}  placeholder='Password' id="password" onChange={this.getPasswordLogin}/>
                <button onClick={this.changevalueLogin}>
                    {isShowns===true ? <IoIosEyeOff/> : <IoMdEye/>}
                </button>
                <button type="submit">Login</button>
            </form>
        )
    }
}

export default Login
