import {useState,useEffect} from 'react'
import {Navigate} from 'react-router-dom'
import { FaTrashAlt } from "react-icons/fa";

const DisplayData = props => {
    const {datas,onDelete} = props 
    const{id,username,email,location,gender,occupation,number} = datas
    const [isEdited,changeEdit] = useState(false)
    const editTheData = () => {
        changeEdit(!isEdited)
    }


    const deleteTheData = async () => {
        const url = `http://localhost:5000/delete/${id}`
        console.log(id)
        const options = {
            method: "DELETE"
        } 
        await fetch(url,options)
        onDelete()
    }

    const renderData = () => {
        if(isEdited===false){
            return(
                <li>
                    <p>{username}</p>
                    <p>{email}</p>
                    <p>{location}</p>
                    <p>{gender}</p>
                    <p>{occupation}</p>
                    <p>{number}</p>
                    <button onClick={editTheData}>Edit</button>
                    <button onClick={deleteTheData}><FaTrashAlt/></button>
                </li>
            )
        }else{
            return(
                <>
                    <form onSubmit={uploadTheData}>
            <label htmlFor="firstName">First Name</label>
            <input type="text" id="firstName" placeholder='First Name'value={firstname} onChange={getFirstName}/>
            <label htmlFor="lastName">Last Name</label>
            <input type="text" id="lastName" placeholder='Last Name'value={lastname} onChange={getLastName}/>
            <label htmlFor="email">Email</label>
            <input type="text" id="email" placeholder='Email' value={email} onChange={getEmail}/>
            <label htmlFor="location">Location</label>
            <input type="text" id="location" placeholder='Location' value={location} onChange={getLocation}/>
            <label htmlFor="occupation">Occupation</label>
            <input type="text" id="occupation" placeholder='Occupation' value={occupation} onChange={getOccupation}/>
            <label htmlFor="number">Number</label>
            <input type="number" id="number" placeholder='Number' value={number} onChange={getNumber}/>
            <label htmlFor='gender'>Gender</label>
            <select id="gender" onChange={getGender}>
                <option>Male</option>
                <option>Female</option>
                <option>other</option>
            </select>
            <button type="submit">Submit</button>
        </form>
                </>
            )
        }
    }
    
    return(
       <>
        {renderData()}
       </>
    )

}
export default DisplayData
