import {Component} from 'react'

class Register extends Component{
    state = {username:"",password:"",email:"",number:""}
    render(){
        const {username,email,number} = this.state

        getUserName = (event) => {
            this.setState({username:event.target.value})
        }

        getPassword = (event) => {
            this.setState({password:event.target.value})
        }

        getEmail = event => {
            this.setState({email:event.target.value})
        }

        getNumber = event => {
            this.setState({number:event.target.value})
        }

        submitData = event => {
            event.preventDefault()
            const {username,password,email,number} = this.state
            

        }

        return(
            <form onSubmit={this.submitData}>
                <label htmlFor="username">USERNAME</label>
                <input type="text" value={username} placeholder='Username should be 14 characters' id="username" onChange={this.getUserName}/>
                <label htmlFor="password">PASSWORD</label>
                <input type="password"  placeholder='Password' id="password" onChange={this.getPassword}/>
                <label htmlFor="email">EMAIL</label>
                <input type="text" value={email} placeholder='Email' id="email" onChange={this.getEmail}/>
                <label htmlFor="number">MOBILE NUMBER</label>
                <input type="number" value={number} placeholder='Mobile Number' id="number" onChange={this.getNumber}/>
                <button type="submit">Submit</button>
            </form>
        )
    }
}
