import { useState, useEffect } from 'react';
import Cookies from 'js-cookie'
import { FaTrashAlt } from "react-icons/fa";

const DisplayData = ({ datas, onDelete, onUpdate }) => {
    const { id, username, email, location, gender, occupation, number } = datas;
    const [isChanged, setChange] = useState(false);

    
    const [usernames, setFirstName] = useState(username);
    const [emails, setEmail] = useState(email);
    const [locations, setLocation] = useState(location);
    const [occupations, setOccupation] = useState(occupation);
    const [numbers, setNumber] = useState(number);
    const [genders, setGender] = useState(gender);

    
    const editTheData = () => {
        setChange(true);
    };


    useEffect(() => {
        setFirstName(username);
        setEmail(email);
        setLocation(location);
        setOccupation(occupation);
        setNumber(number);
        setGender(gender);
    }, [username, email, location, occupation, number, gender]);

    
    const uploadTheDatas = async (event) => {
        event.preventDefault();
        const user_id = Cookies.get('user_id');
        const url = "http://localhost:5000/update/";
        const newData = {
            id,
            user_id,
            username: usernames,
            email: emails,
            location: locations,
            gender: genders,
            occupation: occupations,
            number: numbers
        };
        const options = {
            method: "PUT",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify(newData)
        };
        const response = await fetch(url, options);
        if(response.ok===true){
            setChange(false)
        }
    };


    const deleteTheData = async () => {
        const url = `http://localhost:5000/delete/${id}`;
        const options = { method: "DELETE" };
        await fetch(url, options);
        onDelete();
    };

    
    const renderData = () => {
        if (isChanged===false) {
            return (
                <li>
                    <p>{username}</p>
                    <p>{email}</p>
                    <p>{location}</p>
                    <p>{gender}</p>
                    <p>{occupation}</p>
                    <p>{number}</p>
                    <button onClick={editTheData}>Edit</button>
                    <button onClick={deleteTheData}><FaTrashAlt /></button>
                </li>
            );
        } else {
            return (
                <form onSubmit={uploadTheDatas}>
                    <label htmlFor="firstName">Username</label>
                    <input type="text" id="firstName" value={usernames} onChange={(e) => setFirstName(e.target.value)} />
                    <label htmlFor="email">Email</label>
                    <input type="text" id="email" value={emails} onChange={(e) => setEmail(e.target.value)} />
                    <label htmlFor="location">Location</label>
                    <input type="text" id="location" value={locations} onChange={(e) => setLocation(e.target.value)} />
                    <label htmlFor="occupation">Occupation</label>
                    <input type="text" id="occupation" value={occupations} onChange={(e) => setOccupation(e.target.value)} />
                    <label htmlFor="number">Number</label>
                    <input type="number" id="number" value={numbers} onChange={(e) => setNumber(e.target.value)} />
                    <label htmlFor="gender">Gender</label>
                    <input type="text" id="gender" value={genders} onChange={(e) => setGender(e.target.value)} />
                    <button type="submit">Update</button>
                </form>
            );
        }
    };

    return <>{renderData()}</>;
};

export default DisplayData;
