import {useState,useEffect} from 'react'
import {Navigate} from 'react-router-dom'
import { FaTrashAlt } from "react-icons/fa";

const DisplayData = props => {
    const {datas,onDelete} = props 
    const{id,username,email,location,gender,occupation,number} = datas
    const [isEdited,changeEdit] = useState(false)
    const editTheData = () => {
        changeEdit(!isEdited)
    }


    const deleteTheData = async () => {
        const url = `http://localhost:5000/delete/${id}`
        console.log(id)
        const options = {
            method: "DELETE"
        } 
        await fetch(url,options)
        onDelete()
    }

    const renderData = () => {
        if(isEdited===false){
            return(
                <li>
                    <p>{username}</p>
                    <p>{email}</p>
                    <p>{location}</p>
                    <p>{gender}</p>
                    <p>{occupation}</p>
                    <p>{number}</p>
                    <button onClick={editTheData}>Edit</button>
                    <button onClick={deleteTheData}><FaTrashAlt/></button>
                </li>
            )
        }else{
            return(
                <>
                    <h1>hi</h1>
                </>
            )
        }
    }
    
    return(
       <>
        {renderData}
       </>
    )

}
export default DisplayData
