import {Component} from 'react'
import {v4 as uuidv4} from 'uuid'
import {useNavigate} from 'react-router-dom'

class Register extends Component{
    state = {username:"",password:"",email:"",number:"",errMsg:""}

    

    getUserName = (event) => {
        this.setState({username:event.target.value})
    }

    getPassword = (event) => {
        this.setState({password:event.target.value})
    }

    getEmail = event => {
        this.setState({email:event.target.value})
    }

    getNumber = event => {
        this.setState({number:event.target.value})
    }

    submitData = async event => {
        event.preventDefault()
        const {username,password,email,number} = this.state
        const data = {
            "id": uuidv4(),
            "username":username,
            "password":password,
            "email":email,
            "number":number
        }
        const url = "http://localhost:5000/register"
        const options = {
            method: "POST",
            headers: {
                "Content-Type": "application/json"
            },
            body: JSON.stringify(data)
        }
        const respone = await fetch(url,options)
        if(respone.ok===true){
            const responseData = await respone.json()
            if(responseData.message==="success"){
                history.replace("/login")
                this.setState({errMsg:""})
            }else{
                this.setState({errMsg:responseData.message})
            }
        }
        this.setState({username:"",email:"",number:""})
        
    }
    render(){
        const {username,email,number} = this.state

        

        return(
            <form onSubmit={this.submitData}>
                <label htmlFor="username">USERNAME</label>
                <input type="text" value={username} placeholder='Username should be 14 characters' id="username" onChange={this.getUserName}/>
                <label htmlFor="password">PASSWORD</label>
                <input type="password"  placeholder='Password' id="password" onChange={this.getPassword}/>
                <label htmlFor="email">EMAIL</label>
                <input type="text" value={email} placeholder='Email' id="email" onChange={this.getEmail}/>
                <label htmlFor="number">MOBILE NUMBER</label>
                <input type="number" value={number} placeholder='Mobile Number' id="number" onChange={this.getNumber}/>
                <button type="submit">Submit</button>
            </form>
        )
    }
}

export default Register
