import {Component} from 'react'
import {v4 as uuidv4} from 'uuid'
import { Navigate } from 'react-router-dom';
import { IoMdEye } from "react-icons/io";
import { IoIosEyeOff } from "react-icons/io";
import {Label,Input,Container,Form,LoginButton,Select, SeeButton,InputPassword,LabelEmail,LabelNumber} from "./styledComponents"

class Register extends Component{
    state = {username:"",password:"",email:"",number:"",errMsg:"",isShown:false,shouldRedirect:false}


    changevalueLogin = (event) => {
        event.preventDefault()
        this.setState((prev)=>({
            isShown:!prev.isShown
        }))
    }


    getUserName = (event) => {
        this.setState({username:event.target.value})
    }

    getPassword = (event) => {
        this.setState({password:event.target.value})
    }

    getEmail = event => {
        this.setState({email:event.target.value})
    }

    getNumber = event => {
        this.setState({number:event.target.value})
    }

    submitData = async event => {
        event.preventDefault()
        const {username,password,email,number} = this.state
        const opdata = {
            id: uuidv4()
        }
    
        if (username) {
            opdata.username = username
        }
    
        if (password) {
            opdata.password = password
        }
    
        if (email) {
            opdata.email = email
        }
    
        if (number) {
            opdata.number = number
        }
        const url = "http://localhost:5000/register/"
        const options = {
            method: "POST",
            headers: {
                "Content-Type": "application/json"
            },
            body: JSON.stringify(opdata)
        }
        const respone = await fetch(url,options)
        console.log(respone)
        if(respone.ok===true){
            const responseData = await respone.json()
            console.log(responseData)
            if(responseData.message==="success"){
                this.setState({errorMsg:"",shouldRedirect:true})   
            }else{
                this.setState({errorMsg:responseData.message})
            }
        }
        
        
    }
    render(){
        const {username,email,number,errorMsg,isShown,shouldRedirect} = this.state

        if(shouldRedirect){
            return <Navigate to="/login" replace/>
        }

        return(
            <Container>
                <Form onSubmit={this.submitData}>
                    <Label htmlFor="username">USERNAME</Label>
                    <Input type="text" value={username} placeholder='Username should be 14 characters' id="username" onChange={this.getUserName}/>
                    <Label htmlFor="password">PASSWORD</Label>
                    <div>
                        <InputPassword type={isShown===true ? "text" : "password"}  placeholder=' Password' id="password" onChange={this.getPasswordLogin}/>
                        <SeeButton onClick={this.changevalueLogin}>
                            {isShown===true ? <IoIosEyeOff class="sizeTheIcon"/> : <IoMdEye class="sizeTheIcon"/>}
                        </SeeButton>
                    </div>
                    <LabelEmail htmlFor="email">EMAIL</LabelEmail>
                    <Input type="text" value={email} placeholder='Email' id="email" onChange={this.getEmail}/>
                    <LabelNumber htmlFor="number">MOBILE NUMBER</LabelNumber>
                    <Input type="number" value={number} placeholder='Mobile Number' id="number" onChange={this.getNumber}/>
                    <LoginButton type="submit">Submit</LoginButton>
                    <p className='error'>{errorMsg}</p>
                </Form>
            </Container>
        )
    }
}

export default Register
