import { Component } from "react";
import Cookies from 'js-cookie'
import {Navigate} from 'react-router-dom'

class Home extends Component{
    state = {moveBack:false,goTOAddSection:false}

    componentDidMount(){
        this.getData()
    }

    getData = async () => {
        const username = Cookies.get('username')
        const encodeUserName = encodeURIComponent(username)
        console.log(encodeUserName)
        const url = `http://localhost:5000/user_data/?username=${encodeUserName}`
        const options = {
            method: "GET"
        }
        const response = await fetch(url,options)
        const responseData = await response.json()
        Cookies.set('user_id',responseData.id.id)
    }

    moveToLogin = () => {
        Cookies.remove("jwt_token")
        Cookies.remove("username")
        Cookies.remove("user_id")
        this.setState({moveBack:true})
    }

    addNewData = () => {
        this.setState({goTOAddSection:true})
    }

    render(){
        const {moveBack,goTOAddSection} = this.state
        if(goTOAddSection){
            return <Navigate to='/details_form' replace/>
        }
        if(moveBack){
            return <Navigate to='/login' replace/> 
        }
        return(
           <>
            <nav>
                <button onClick={this.moveToLogin}>Logout</button>
                <button onClick={this.addNewData}>Add</button>
            </nav>
            <div>
                
            </div>
           </>
        )
    }
}

export default Home
